mapper.select:
SELECT memberNo, memberName, memberId, memberNick FROM member
WHERE 1=1
	AND memberNo = #{no }

mapper.update:
UPDATE member
SET memberNick = #{memberNick }, memberPw = #{memberPw }, memberBirth = #{memberBirth }, memberTel = #{memberTel }, postCode = #{postCode }, roadAddress = #{roadAddress }, jibunAddress = #{jibunAddress }, detailAddress = #{detailAddress }, extraAddress = #{extraAddress }
WHERE memberNo = #{memberNo }

