mapper.select:
SELECT count(*) FROM ${tableName}



			AND title LIKE '%' || #{search} || '%'


			AND category = #{category}


			AND novelNo = #{novelNo}

select * from (
	select rownum rnum, B.* FROM (
		select novel.*, (
			SELECT membername FROM member WHERE novel.memberNo = memberno
		) AS memberName from novel



			AND title LIKE '%' || #{search} || '%'


			AND category = #{category}



		order by novelNo desc

		) B ORDER BY rnum
) BOARD WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT novel.*, (
	SELECT membername FROM member WHERE novel.memberNo = memberno
) AS memberName
FROM novel WHERE novelNo = #{novelNo}

	 SELECT * FROM (
     	SELECT novel.*, (
			SELECT membername FROM member WHERE novel.memberNo = memberno
		) AS memberName FROM novel

	     	WHERE category = #{category}

	     	ORDER BY score DESC
     ) WHERE ROWNUM BETWEEN 1 AND 3

     	select * from (
			select rownum rnum, B.* FROM (
				select * from episode WHERE novelNo = #{novelNo} order by novelNo desc
			) B ORDER BY rnum
		) BOARD WHERE rnum BETWEEN #{startNo} AND #{endNo}
