mapper.select:
SELECT b.bookName, b.bookWriter, b.bookNo, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade
FROM book b
WHERE b.bookNo = #{bookNo}

SELECT b.bookno, b.bookName, b.bookWriter, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade
FROM book b
WHERE b.bookNo=#{bookNo}

SELECT b.bookNo, b.bookName, b.bookWriter, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade FROM (SELECT rownum rnum, B.* FROM(SELECT * FROM book ORDER BY bookNo DESC)B ORDER BY rnum)b WHERE 1=1 AND
b.bookName like '%'||#{keyword}||'%' OR b.bookWriter like '%'||#{keyword}||'%' AND
rnum BETWEEN #{startNo} AND #{endNo}
