mapper.select:
SELECT b.bookName, b.bookWriter, b.bookNo, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade
FROM book b
WHERE b.bookNo = #{bookNo}

SELECT r.feedNo, r.bookNo, r.memberNo, r.review, r.privacy, r.reviewDate, b.bookName, b.bookWriter, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade, m.memberId, m.memberNick
FROM (SELECT rownum rnum, B.* FROM(SELECT * FROM review_sns ORDER BY feedNo DESC)B ORDER BY rnum)r, book b, member m
WHERE rnum BETWEEN #{startNo} AND #{endNo} AND r.bookNo = b.bookNo AND m.memberNo = r.memberNo AND r.bookNo = #{bookNo}

SELECT b.bookno, b.bookName, b.bookWriter, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade
FROM book b
WHERE b.bookNo=#{bookNo}

SELECT m.memberId, m.memberNick
FROM member m, review_sns r
WHERE m.memberNo = r.memberNo AND m.memberNo = #{memberNo}

SELECT b.bookNo, b.bookName, b.bookWriter, b.bookPublisher, b.publishingYear, b.bookContent, b.genreNo, b.grade FROM (SELECT rownum rnum, B.* FROM(SELECT * FROM book ORDER BY bookNo DESC)B ORDER BY rnum)b WHERE 1=1 AND
b.bookName like '%'||#{keyword}||'%' OR b.bookWriter like '%'||#{keyword}||'%' AND
rnum BETWEEN #{startNo} AND #{endNo}
